import styled from "styled-components";

export const PlanWrapper=styled.div`
  .todo-item{
    border-top:1px solid #ccc;
    height:50px;
    padding:15px 0px;
  }
  .todo-item::after{
    content:'';
    display:table;
    clear:both;
  }
  .todo-item:last-child{
    border-bottom:1px solid #ccc;
  }
  .todo-item input{
    float:left;
    padding:10px 0px;
    margin-right:10px;
  }
  .todo-item div{
    float:left;
    margin-right:50px;
  }`

export const MoneyTotal=styled.div`
border-bottom:1px soild #ccc;
  span{
    display:block;
    float:left;
    margin-right:20px
  }
  span::after{
    content:'';
    display:table;
    clear:both;
  }`
